import React, { useCallback, useContext, useEffect, useMemo, useRef, useState } from "react";
import {
	Dimensions,
	StyleSheet,
	TouchableOpacity,
	View,
	Text,
	Image,
	TextInput,
	Button,
	ActivityIndicator,
} from "react-native";
import {
	TicketIcon,
	Notification,
	Person,
	Chat,
	Direction,
	Warning,
	ScanTicket,
	Icon,
} from "../constants/icons";
import { StackActions, useNavigation } from "@react-navigation/native";
import { SettingsButton } from "../Components/settingsButton";
import { ModalPopUp } from "../Components/Modal";
import { COLORS } from "../constants/theme";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { Pressable } from "react-native";
import { ErrorMessage, Formik } from "formik";
import * as Yup from "yup";
import axios from "axios";
import { FormSubmitBtn } from "../Components/FormSubmitBtn";
import BottomSheet from "../Components/Bottomsheet";
const { height, width } = Dimensions.get("screen");

export const Profile = () => {

		const [actionTriggered, setActionTriggered] = useState("");
		const [action, setAction] = useState("");
	        const [isLoading, setIsLoading] = useState(false);
			const bottomSheetRef = useRef();
	  const pressHandler = useCallback(() => {
		bottomSheetRef.current.expand();
	  }, []);

	
		const [visible, setVisible] = useState(false);
          const [userInfo, setUserInfo] = useState(null)
          const [userToken, setUserToken] = useState(null)


  const  getData = async () => {
	try {
	  const value = await AsyncStorage.getItem('userInfo')
	  const userToken = await AsyncStorage.getItem("userToken")

	  if(value !== null) {
		setUserInfo(JSON.parse(value))
		setUserToken(userToken)
	  }
	} catch(e) {
	  console.log(`${e}`)
	}
  }

   useEffect(()=>{
	getData()
	},[])

	const navigation = useNavigation();


	const verifyPassword = async(values)=>{

		const email = userInfo?.email
	     const token = userToken
		const config = {
			headers: { Authorization: `Bearer ${token}` }
		};
		const body={
			email:email,
			password:values.password
		}
		

		setIsLoading(true)
	   axios.post("https://code-6z3x.onrender.com/api/tickets/verifyPassword",body,config
		).then((res)=>{
			if(res.status === 200){
				setVisible(false)
			setIsLoading(false)
			navigation.navigate("TicketScreen")
			}
		}).catch((e)=>{
			console.log(`${e}`)
		})
		// since i want to use the same functionality to dispatch the users, if the userrole is 1, it would navigate the user to
		// scantickets, else navigate the user to check tickets
	   }

	   const validationSchema = Yup.object({
		password: Yup.string()
		.trim()
		.min(8, "Password not long enough!")
		.required("Password required!"),
	});


	// here, i'm getting the whole userinfo to get the values
	return (

		<View style={styles.container}>
			<View
				style={{
					width: width - 20,
				}}
			>
				<View style={{ flexDirection: "row", marginLeft: 10 }}>
					<View style={styles.itemcontainer}>
						<Text style={{ fontWeight: "600", fontSize: 30, color: COLORS.white, fontFamily:"Poppins", color:"#ececec" }}>
						{userInfo?.firstname.charAt(0).toUpperCase()}{userInfo?.lastname.charAt(0).toUpperCase()}
						</Text>
					</View>
					<View style={{ paddingBottom: 10, paddingLeft: 10 }}>
						<Text style={{ fontSize: 24, fontWeight: "600", fontFamily:"Poppins", color:"#363636" }}>{userInfo?.firstname} {userInfo?.lastname}</Text>
						<Text style={styles.email}>{userInfo?.email}</Text>
					</View>
				</View>
				<View>
					<TouchableOpacity
						activeOpacity={0.7}
						onPress={() => navigation.navigate("AccountScreen")}
					>
						<SettingsButton
							iconLeft={Direction}
							icon={Person}
							ButtonName={"Account"}
						/>
					</TouchableOpacity>
					<TouchableOpacity
						activeOpacity={0.7}
						onPress={() => navigation.navigate("NotificationScreen")}
					>
						<SettingsButton
							iconLeft={Direction}
							icon={Notification}
							ButtonName={"Notification"}
						/>
					</TouchableOpacity>
					<TouchableOpacity
						activeOpacity={0.7}
						onPress={() => {
							setVisible(true);
							setActionTriggered("Action_1");
						}}
					>
						<SettingsButton icon={TicketIcon} ButtonName={"Ticket"} />
					</TouchableOpacity>
					
					<TouchableOpacity
						activeOpacity={0.7}
						onPress={() => {
							setVisible(true);
							setActionTriggered("Action_2");
						}}
					>
						<SettingsButton icon={Chat} ButtonName={"Feedback"} />
					</TouchableOpacity>
                     
					 
					 { userInfo?.role === 0?
					 
					 (
						<>
					 <TouchableOpacity
						activeOpacity={0.7}
						onPress={() => {
							// setVisible(true);
							//  setActionTriggered("Action_3")
							navigation.navigate("ScanTicketScreen")
							}}
					>
						
                         <SettingsButton
							icon={ScanTicket}
							ButtonName={"Scan QR Code"}
						/>
					</TouchableOpacity>

					<View style={{width:55, height:55, backgroundColor:"#004fc7", borderRadius:50, position:"absolute", bottom:-200, right:15}}>
                     <Pressable
					// onPress={() => {
					// 	setVisible(true);
					// 	setActionTriggered("Action_4");
					// }}
					onPress={()=>pressHandler()}
					>
                     <Text style={{bottom:7, fontFamily:"Poppins", alignSelf:"center", fontSize:48, color:"#fff"}}>+</Text>
					</Pressable>
					</View>
					</>
	)
					:
		null
}


                 <BottomSheet
						ref={bottomSheetRef}
						activeHeight={height * 0.5}
						backgroundColor={"#fff"}
						// backDropColor={"black"}
					>
						<View
							style={{
								width: width,
								height: 200,
								marginLeft: 20,
								marginTop: 20,
							}}
						>
							<View>
								<Text
									style={{
										fontWeight: "600",
										fontFamily: "Poppins2",
										fontSize: 30,
									}}
								>
									Create
								</Text>

								<View style={{ paddingVertical: 10 }}>
									<TouchableOpacity
										onPress={() => {
											setVisible(false);
											navigation.navigate("UploadPostScreen");
										}}
									>
										<View style={{ flexDirection: "row" }}>
											<Icon name={"newspaper-outline"} size={20} />
											<Text style={styles.text}>New Post</Text>
										</View>
									</TouchableOpacity>
									<TouchableOpacity
										onPress={() => {
											setVisible(false);
